sys_variable_value.value:
/*global tf_config, tf_workspace, tf_run, tf_terraform_record, tf_vcs_record, tf_http, tf_variable, tf_util*/

gs.info("Workflow: Terraform Provision Workspace with Variables Started...");

// ** Set to the Variable Set for this Catalog Item **
// ** Any variables in the set must start with `tf_`
var VAR_SET_NAME = "manage_my_vm_size_variable_set";
var tf_variables = current.variables[VAR_SET_NAME];
//tf_variables.tf_var_gid = current.variables.tf_var_gid.email;

//gs.info("Value is-"+ tf_variables +"--TYPE-"+ typeof tf_variables);



/* Instantiate Terraform Classes */
var conf               = new tf_config();
var util               = new tf_util();
var workspace          = new tf_workspace();
var terraform_record   = new tf_terraform_record();
var vcs_record         = new tf_vcs_record();
var terraform_variable = new tf_variable();

/* Variables from the Request */
var requestItemNumber = current.number;
var requestID         = current.request.getDisplayValue();       // Catalog Request ID
var workspaceVCS      = 'Manage Size of my VM'; // Variable from Catalog Request
var description       = current.variables.description;

/* Data for the request content */
var workspaceName  = workspace.createName(requestItemNumber);
var workspaceURL   = workspace.createURL(conf, workspaceName);
var requestItemURL = terraform_record.createRecordURL(current.sys_id);
var vcsRecord      = vcs_record.get(workspaceVCS);

/* Create request content */
var content        = workspace.createRequestContent(vcsRecord, workspaceName, requestItemNumber, requestItemURL, description, false, true);
var requestContent = JSON.stringify(content);

/* HTTP REST request to Terraform API */
var http               = new tf_http("TF Workspaces", "Create Workspace", requestContent);
var workspace_response = http.execute();
var workspace_id       = workspace_response.data.id;

/* Create SN Record from response */
var gr = terraform_record.create(workspace_response, current.request.sys_id, current.sys_id, workspaceURL);

var comment = util.formatCommentBlockQuote("Terraform: Workspace Created</br>provisioning resources...");
terraform_record.postRITMComment(gr.requested_item, comment);

// Create any TF Variables/ENV Variables from the Variable Set.
terraform_variable.createVariablesFromSet(tf_variables, workspace_id);
//terraform_variable.createVariablesFromSet(tf_variables1, workspace_id);

// Create a run for workspace
// Workspace was created with "auto-apply"
util.sleep(5000);

var run               = new tf_run();
var runContent        = run.createRunRequestContent(workspace_id, requestID);
var runRequestContent = JSON.stringify(runContent);

/* HTTP REST request to Terraform API */
var runHTTP  = new tf_http("TF Runs", "Create Run", runRequestContent);
runHTTP.execute();

gs.info("Workflow: Terraform Provision Workspace with Variables Finished.");

